import React from 'react'
import { useDispatch, useSelector } from 'react-redux';

import { logout, selectUser } from './Reducer/userReducer';
function Logout() {
    const dispatch=useDispatch();
    const user=useSelector(selectUser);
   const  handleLogout=(e)=>{
        e.preventDefault();


        dispatch(logout());
    }
    return (
        <div style={{ width: "100%", height: "100vh", backgroundImage: "linear-gradient(#bdc3c7, #2c3e50)" }}>
                <div style={{ display: "flex", flexDirection: "column", width: "100%", justifyContent: "center", alignItems: "center", height: "100vh" }}>


            <h1>Hello {user.name}</h1>
            <h3>Welcome to Reactjs With Redux</h3>
            <button  style={{ cursor:"pointer", width: "250px",height: "25px",    border: "none",    borderRadius: "6px",    padding: "5px 0px 5px 20px"}} onClick={(e)=>handleLogout(e)}>Logout</button>

        </div>
</div>



    )

}

export default Logout;
